SELECT * FROM complete_cast AS cc, comp_cast_type AS cct1, title AS t, movie_info_idx AS mi_idx WHERE cct1.kind = 'cast' AND t.production_year > 2000 AND t.id = cc.movie_id AND cc.movie_id = t.id AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND cc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = cc.movie_id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id;