SELECT * FROM info_type AS it, movie_info AS mi, complete_cast AS cc, comp_cast_type AS cct1 WHERE cct1.kind = 'cast' AND it.info = 'release dates' AND mi.info IS NOT NULL AND (mi.info LIKE 'Japan:%200%' OR mi.info LIKE 'USA:%200%') AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id AND it.id = mi.info_type_id AND mi.info_type_id = it.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id;