SELECT * FROM kind_type AS kt2, link_type AS lt, movie_link AS ml, title AS t2 WHERE kt2.kind IN ('tv series', 'episode') AND lt.link IN ('sequel', 'follows', 'followed by') AND t2.production_year BETWEEN 2000 AND 2010 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND t2.id = ml.linked_movie_id AND ml.linked_movie_id = t2.id AND kt2.id = t2.kind_id AND t2.kind_id = kt2.id;