SELECT * FROM cast_info AS ci, role_type AS rt, title AS t, aka_name AS an WHERE ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND rt.role = 'actress' AND ci.movie_id = t.id AND t.id = ci.movie_id AND ci.role_id = rt.id AND rt.id = ci.role_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;