SELECT * FROM title AS t, movie_companies AS mc, movie_keyword AS mk, keyword AS k WHERE k.keyword = 'computer-animation' AND t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2005 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;