SELECT * FROM movie_keyword AS mk, cast_info AS ci, complete_cast AS cc, comp_cast_type AS cct2 WHERE cct2.kind = 'complete+verified' AND ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;