SELECT * FROM keyword AS k, movie_keyword AS mk, movie_info_idx AS mi_idx, info_type AS it WHERE it.info = 'rating' AND k.keyword LIKE '%sequel%' AND mi_idx.info > '9.0' AND mk.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mk.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id AND it.id = mi_idx.info_type_id AND mi_idx.info_type_id = it.id;