SELECT * FROM movie_keyword AS mk, movie_info AS mi, keyword AS k, title AS t WHERE k.keyword = 'sequel' AND mi.info IN ('Germany', 'German') AND t.production_year BETWEEN 2000 AND 2010 AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND mi.movie_id = t.id AND t.id = mi.movie_id AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id;