SELECT * FROM company_name AS cn, movie_companies AS mc, movie_info_idx AS mi_idx, info_type AS it2 WHERE cn.name LIKE 'Lionsgate%' AND it2.info = 'votes' AND mc.note LIKE '%(Blu-ray)%' AND mi_idx.movie_id = mc.movie_id AND mc.movie_id = mi_idx.movie_id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id AND cn.id = mc.company_id AND mc.company_id = cn.id;