SELECT * FROM title AS t, cast_info AS ci, role_type AS rt, char_name AS chn WHERE ci.note LIKE '%(producer)%' AND rt.role = 'actor' AND t.production_year > 2010 AND t.id = ci.movie_id AND ci.movie_id = t.id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND rt.id = ci.role_id AND ci.role_id = rt.id;