SELECT * FROM title AS t, movie_info AS mi, company_name AS cn, movie_companies AS mc WHERE cn.country_code = '[us]' AND mi.info LIKE 'USA:%200%' AND t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2005 AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id;