SELECT * FROM complete_cast AS cc, cast_info AS ci, aka_name AS an, comp_cast_type AS cct1 WHERE cct1.kind = 'cast' AND ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id;