SELECT * FROM comp_cast_type AS cct2, complete_cast AS cc, title AS t, movie_link AS ml WHERE cct2.kind LIKE 'complete%' AND t.production_year BETWEEN 1950 AND 2010 AND ml.movie_id = t.id AND t.id = ml.movie_id AND t.id = cc.movie_id AND cc.movie_id = t.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id AND ml.movie_id = cc.movie_id AND cc.movie_id = ml.movie_id;