SELECT * FROM movie_info AS mi, aka_title AS at, info_type AS it1, title AS t WHERE it1.info = 'release dates' AND mi.note LIKE '%internet%' AND mi.info LIKE 'USA:% 200%' AND t.production_year BETWEEN 2005 AND 2010 AND t.id = at.movie_id AND at.movie_id = t.id AND t.id = mi.movie_id AND mi.movie_id = t.id AND mi.movie_id = at.movie_id AND at.movie_id = mi.movie_id AND it1.id = mi.info_type_id AND mi.info_type_id = it1.id;