SELECT * FROM movie_companies AS mc, company_name AS cn, aka_title AS at, movie_keyword AS mk WHERE cn.country_code = '[us]' AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id AND mk.movie_id = at.movie_id AND at.movie_id = mk.movie_id AND mc.movie_id = at.movie_id AND at.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id;