SELECT * FROM movie_companies AS mc, company_name AS cn, role_type AS rt, cast_info AS ci WHERE cn.country_code = '[us]' AND rt.role = 'writer' AND mc.company_id = cn.id AND cn.id = mc.company_id AND ci.role_id = rt.id AND rt.id = ci.role_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id;