SELECT * FROM title AS t, cast_info AS ci, role_type AS rt, char_name AS chn, company_type AS ct, movie_companies AS mc WHERE ci.note LIKE '%(producer)%' AND rt.role = 'actor' AND t.production_year > 2010 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = ci.movie_id AND ci.movie_id = t.id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND rt.id = ci.role_id AND ci.role_id = rt.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id;