SELECT * FROM movie_companies AS mc, role_type AS rt, cast_info AS ci, char_name AS chn WHERE ci.note LIKE '%(producer)%' AND rt.role = 'actor' AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND rt.id = ci.role_id AND ci.role_id = rt.id;