SELECT * FROM company_name AS cn, movie_companies AS mc, cast_info AS ci, role_type AS rt, title AS t, char_name AS chn WHERE ci.note LIKE '%(producer)%' AND cn.country_code = '[us]' AND t.production_year > 1990 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = ci.movie_id AND ci.movie_id = t.id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND rt.id = ci.role_id AND ci.role_id = rt.id AND cn.id = mc.company_id AND mc.company_id = cn.id;