SELECT * FROM link_type AS lt, movie_link AS ml, movie_companies AS mc, company_name AS cn WHERE cn.country_code != '[pl]' AND (cn.name LIKE '20th Century Fox%' OR cn.name LIKE 'Twentieth Century Fox%') AND mc.note IS NOT NULL AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND mc.company_id = cn.id AND cn.id = mc.company_id AND ml.movie_id = mc.movie_id AND mc.movie_id = ml.movie_id;