SELECT * FROM title AS t, movie_companies AS mc, company_type AS ct, company_name AS cn, movie_keyword AS mk WHERE cn.country_code != '[pl]' AND ct.kind != 'production companies' AND ct.kind IS NOT NULL AND mc.note IS NOT NULL AND t.production_year > 1950 AND t.id = mk.movie_id AND mk.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.company_type_id = ct.id AND ct.id = mc.company_type_id AND mc.company_id = cn.id AND cn.id = mc.company_id AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id;