SELECT * FROM movie_link AS ml, movie_companies AS mc, company_type AS ct, company_name AS cn WHERE cn.country_code != '[pl]' AND ct.kind != 'production companies' AND ct.kind IS NOT NULL AND mc.note IS NOT NULL AND mc.company_type_id = ct.id AND ct.id = mc.company_type_id AND mc.company_id = cn.id AND cn.id = mc.company_id AND ml.movie_id = mc.movie_id AND mc.movie_id = ml.movie_id;