SELECT * FROM info_type AS it1, movie_info AS mi, movie_companies AS mc, company_type AS ct, movie_info_idx AS mi_idx, company_name AS cn WHERE cn.country_code = '[us]' AND ct.kind = 'production companies' AND it1.info = 'genres' AND mi.info IN ('Drama', 'Horror') AND mi_idx.info > '8.0' AND mi.info_type_id = it1.id AND it1.id = mi.info_type_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND cn.id = mc.company_id AND mc.company_id = cn.id AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mi.movie_id;