SELECT * FROM company_type AS ct, company_name AS cn, movie_info_idx AS mi_idx, movie_companies AS mc WHERE cn.country_code = '[us]' AND ct.kind = 'production companies' AND mi_idx.info > '8.0' AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND cn.id = mc.company_id AND mc.company_id = cn.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id;