SELECT * FROM title AS t, movie_companies AS mc, company_type AS ct, movie_info_idx AS mi_idx WHERE ct.kind IS NOT NULL AND (ct.kind = 'production companies' OR ct.kind = 'distributors') AND t.production_year > 2000 AND (t.title LIKE 'Birdemic%' OR t.title LIKE '%Movie%') AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id;