SELECT * FROM movie_info_idx AS mi_idx, info_type AS it2, title AS t, movie_info AS mi, info_type AS it1 WHERE it1.info = 'budget' AND it2.info = 'bottom 10 rank' AND t.production_year > 2000 AND (t.title LIKE 'Birdemic%' OR t.title LIKE '%Movie%') AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND mi.info_type_id = it1.id AND it1.id = mi.info_type_id AND mi_idx.info_type_id = it2.id AND it2.id = mi_idx.info_type_id AND mi.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mi.movie_id;