SELECT * FROM info_type AS it2, title AS t, movie_info_idx AS mi_idx, movie_companies AS mc WHERE it2.info = 'bottom 10 rank' AND t.production_year > 2000 AND (t.title LIKE 'Birdemic%' OR t.title LIKE '%Movie%') AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND mi_idx.info_type_id = it2.id AND it2.id = mi_idx.info_type_id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id;