SELECT * FROM movie_info_idx AS mi_idx, info_type AS it2, title AS t, movie_companies AS mc, company_type AS ct, company_name AS cn WHERE cn.country_code = '[us]' AND ct.kind = 'production companies' AND it2.info = 'rating' AND mi_idx.info > '7.0' AND t.production_year BETWEEN 2000 AND 2010 AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND mi_idx.info_type_id = it2.id AND it2.id = mi_idx.info_type_id AND t.id = mc.movie_id AND mc.movie_id = t.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND cn.id = mc.company_id AND mc.company_id = cn.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id;