SELECT * FROM movie_info_idx AS mi_idx, info_type AS it2, movie_companies AS mc, company_type AS ct WHERE ct.kind = 'production companies' AND it2.info = 'rating' AND mi_idx.info > '7.0' AND mi_idx.info_type_id = it2.id AND it2.id = mi_idx.info_type_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND mc.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mc.movie_id;