SELECT * FROM movie_info_idx AS mi_idx, info_type AS it2, movie_info AS mi WHERE it2.info = 'rating' AND mi.info IN ('Drama', 'Horror', 'Western', 'Family') AND mi_idx.info > '7.0' AND mi_idx.info_type_id = it2.id AND it2.id = mi_idx.info_type_id AND mi.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mi.movie_id;