SELECT * FROM movie_info_idx AS miidx, info_type AS it, movie_companies AS mc, company_type AS ct, movie_info AS mi, title AS t WHERE ct.kind = 'production companies' AND it.info = 'rating' AND mi.movie_id = t.id AND t.id = mi.movie_id AND mc.movie_id = t.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND miidx.movie_id = t.id AND t.id = miidx.movie_id AND it.id = miidx.info_type_id AND miidx.info_type_id = it.id AND mi.movie_id = miidx.movie_id AND miidx.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND mc.movie_id = mi.movie_id AND miidx.movie_id = mc.movie_id AND mc.movie_id = miidx.movie_id;