SELECT * FROM title AS t, company_type AS ct, movie_companies AS mc, movie_info AS mi WHERE ct.kind = 'production companies' AND t.title != '' AND (t.title LIKE '%Champion%' OR t.title LIKE '%Loser%') AND mi.movie_id = t.id AND t.id = mi.movie_id AND mc.movie_id = t.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND mi.movie_id = mc.movie_id AND mc.movie_id = mi.movie_id;