SELECT * FROM movie_info_idx AS miidx, title AS t, movie_info AS mi WHERE t.title != '' AND (t.title LIKE '%Champion%' OR t.title LIKE '%Loser%') AND mi.movie_id = t.id AND t.id = mi.movie_id AND miidx.movie_id = t.id AND t.id = miidx.movie_id AND mi.movie_id = miidx.movie_id AND miidx.movie_id = mi.movie_id;