SELECT * FROM movie_info_idx AS miidx, info_type AS it, title AS t, kind_type AS kt, movie_info AS mi WHERE it.info = 'rating' AND kt.kind = 'movie' AND mi.movie_id = t.id AND t.id = mi.movie_id AND kt.id = t.kind_id AND t.kind_id = kt.id AND miidx.movie_id = t.id AND t.id = miidx.movie_id AND it.id = miidx.info_type_id AND miidx.info_type_id = it.id AND mi.movie_id = miidx.movie_id AND miidx.movie_id = mi.movie_id;