SELECT * FROM movie_info_idx AS miidx, info_type AS it, title AS t, movie_companies AS mc WHERE it.info = 'rating' AND mc.movie_id = t.id AND t.id = mc.movie_id AND miidx.movie_id = t.id AND t.id = miidx.movie_id AND it.id = miidx.info_type_id AND miidx.info_type_id = it.id AND miidx.movie_id = mc.movie_id AND mc.movie_id = miidx.movie_id;