SELECT * FROM kind_type AS kt, keyword AS k, movie_keyword AS mk, title AS t WHERE k.keyword IN ('murder', 'murder-in-title') AND kt.kind = 'movie' AND t.production_year > 2010 AND (t.title LIKE '%murder%' OR t.title LIKE '%Murder%' OR t.title LIKE '%Mord%') AND kt.id = t.kind_id AND t.kind_id = kt.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;