SELECT * FROM movie_keyword AS mk, aka_title AS at, movie_companies AS mc, company_type AS ct WHERE mc.note LIKE '%(200%)%' AND mc.note LIKE '%(worldwide)%' AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id AND mk.movie_id = at.movie_id AND at.movie_id = mk.movie_id AND mc.movie_id = at.movie_id AND at.movie_id = mc.movie_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id;