SELECT * FROM movie_info AS mi, movie_keyword AS mk, aka_title AS at, keyword AS k WHERE mi.note LIKE '%internet%' AND mi.info LIKE 'USA:% 200%' AND mk.movie_id = mi.movie_id AND mi.movie_id = mk.movie_id AND mk.movie_id = at.movie_id AND at.movie_id = mk.movie_id AND mi.movie_id = at.movie_id AND at.movie_id = mi.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;