SELECT * FROM movie_keyword AS mk, title AS t, keyword AS k, aka_title AS at WHERE t.production_year > 2000 AND t.id = at.movie_id AND at.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.movie_id = at.movie_id AND at.movie_id = mk.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;