SELECT * FROM movie_companies AS mc, aka_title AS at, movie_keyword AS mk WHERE mc.note LIKE '%(200%)%' AND mc.note LIKE '%(worldwide)%' AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id AND mk.movie_id = at.movie_id AND at.movie_id = mk.movie_id AND mc.movie_id = at.movie_id AND at.movie_id = mc.movie_id;