SELECT * FROM movie_info AS mi, title AS t, aka_title AS at WHERE mi.note LIKE '%internet%' AND mi.info LIKE 'USA:% 200%' AND t.production_year > 2000 AND t.id = at.movie_id AND at.movie_id = t.id AND t.id = mi.movie_id AND mi.movie_id = t.id AND mi.movie_id = at.movie_id AND at.movie_id = mi.movie_id;