SELECT * FROM movie_keyword AS mk, movie_companies AS mc, company_type AS ct, keyword AS k WHERE mc.note LIKE '%(200%)%' AND mc.note LIKE '%(worldwide)%' AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id;