SELECT * FROM company_name AS cn, movie_companies AS mc, aka_title AS at, company_type AS ct, title AS t WHERE cn.country_code = '[us]' AND cn.name = 'YouTube' AND mc.note LIKE '%(200%)%' AND mc.note LIKE '%(worldwide)%' AND t.production_year BETWEEN 2005 AND 2010 AND t.id = at.movie_id AND at.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mc.movie_id = at.movie_id AND at.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id;