SELECT * FROM company_name AS cn, movie_companies AS mc, name AS n, movie_keyword AS mk, cast_info AS ci WHERE cn.country_code = '[us]' AND n.id = ci.person_id AND ci.person_id = n.id AND mc.company_id = cn.id AND cn.id = mc.company_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id;