SELECT * FROM title AS t, name AS n, movie_keyword AS mk, cast_info AS ci WHERE t.episode_nr >= 5 AND t.episode_nr < 100 AND n.id = ci.person_id AND ci.person_id = n.id AND ci.movie_id = t.id AND t.id = ci.movie_id AND t.id = mk.movie_id AND mk.movie_id = t.id AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id;