SELECT * FROM movie_companies AS mc, movie_keyword AS mk, keyword AS k, cast_info AS ci WHERE k.keyword = 'character-name-in-title' AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id;