SELECT * FROM movie_companies AS mc, movie_keyword AS mk, cast_info AS ci, keyword AS k, name AS n WHERE k.keyword = 'character-name-in-title' AND n.name LIKE '%B%' AND n.id = ci.person_id AND ci.person_id = n.id AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id;