SELECT * FROM movie_info AS mi, info_type AS it1, title AS t, cast_info AS ci WHERE ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND it1.info = 'genres' AND mi.info IN ('Horror', 'Thriller') AND mi.note IS NULL AND t.production_year BETWEEN 2008 AND 2014 AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = ci.movie_id AND ci.movie_id = t.id AND ci.movie_id = mi.movie_id AND mi.movie_id = ci.movie_id AND it1.id = mi.info_type_id AND mi.info_type_id = it1.id;