SELECT * FROM info_type AS it2, movie_info_idx AS mi_idx, cast_info AS ci, name AS n WHERE ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND it2.info = 'rating' AND mi_idx.info > '8.0' AND n.gender IS NOT NULL AND n.gender = 'f' AND ci.movie_id = mi_idx.movie_id AND mi_idx.movie_id = ci.movie_id AND n.id = ci.person_id AND ci.person_id = n.id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id;