SELECT * FROM movie_companies AS mc, aka_name AS an, cast_info AS ci WHERE ci.note = '(voice)' AND mc.note LIKE '%(200%)%' AND (mc.note LIKE '%(USA)%' OR mc.note LIKE '%(worldwide)%') AND mc.movie_id = ci.movie_id AND ci.movie_id = mc.movie_id AND ci.person_id = an.person_id AND an.person_id = ci.person_id;